import React from "react";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";
import zoomPlugin from 'chartjs-plugin-zoom';

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend, zoomPlugin);

const mockStates = [
    { labels: ["A", "B", "C", "D"], datasets: [{ label: "Node Values", data: [12, 19, 3, 5] }] },
    { labels: ["A", "B", "C", "D"], datasets: [{ label: "Node Values", data: [5, 10, 15, 20] }] },
    { labels: ["A", "B", "C", "D"], datasets: [{ label: "Node Values", data: [20, 5, 10, 8] }] }
];

const options = {
    responsive: true,
    plugins: {
        legend: { position: "top" },
        title: { display: true, text: "Super chart" },
        zoom: {
            pan: { enabled: true, mode: 'xy' },
            zoom: { wheel: { enabled: true }, pinch: { enabled: true }, mode: 'xy' }
        }
    }
};

const App = () => {
    const [chartData, setChartData] = React.useState(mockStates[0]);
    const [cursorPos, setCursorPos] = React.useState(null);
    const chartRef = React.useRef();

    React.useEffect(() => {
        const handleKeyDown = (e) => {
            if (e.key === "1") setChartData(mockStates[0]);
            if (e.key === "2") setChartData(mockStates[1]);
            if (e.key === "3") setChartData(mockStates[2]);
        };
        window.addEventListener("keydown", handleKeyDown);
        return () => window.removeEventListener("keydown", handleKeyDown);
    }, []);

    const handleMouseMove = (event) => {
        const chart = chartRef.current;
        if (!chart) return;
        const points = chart.getElementsAtEventForMode(event.nativeEvent, 'nearest', { intersect: false }, false);
        if (points.length) {
            const point = points[0];
            const label = chartData.labels[point.index];
            const value = chartData.datasets[0].data[point.index];
            setCursorPos({ x: label, y: value });
        } else {
            setCursorPos(null);
        }
    };

    return (
        <div>
            <Line
                ref={chartRef}
                data={chartData}
                options={options}
                onMouseMove={handleMouseMove}
            />
            <p>Ine data pomocou 1 2 3 na klavesnici</p>
            {cursorPos && (
                <p>Cursor XY: {cursorPos.x}, {cursorPos.y}</p>
            )}
        </div>
    );
};

export default App;
