import React from "react";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";
import zoomPlugin from 'chartjs-plugin-zoom';

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend, zoomPlugin);

const mockStates = [
    { labels: ["A", "B", "C", "D", "E", "F", "G", "H"], datasets: [{ label: "Node Values", data: [12, 19, 3, 5, 8, 15, 7, 10], stepped: true }] },
    { labels: ["A", "B", "C", "D", "E", "F", "G", "H"], datasets: [{ label: "Node Values", data: [5, 10, 15, 20, 18, 12, 6, 9], stepped: false }] },
    { labels: ["A", "B", "C", "D", "E", "F", "G", "H"], datasets: [{ label: "Node Values", data: [20, 5, 10, 8, 14, 11, 17, 4] }] }
];

function findPeaks(data) {
    const peaks = [];
    for (let i = 1; i < data.length - 1; i++) {
        if (data[i] > data[i - 1] && data[i] > data[i + 1]) {
            peaks.push({ x: i, y: data[i] });
        }
    }
    return peaks;
}

const getOptions = (zoomEnabled) => ({
    responsive: true,
    plugins: {
        legend: { position: "top" },
        title: { display: true, text: "Super chart" },
        zoom: {
            pan: { enabled: !zoomEnabled, mode: 'xy' },
            zoom: {
                wheel: { enabled: true },
                pinch: { enabled: true },
                mode: 'xy',
                drag: {
                    enabled: zoomEnabled,
                    backgroundColor: 'rgba(0,123,255,0.2)',
                    borderColor: 'rgba(0,123,255,0.5)',
                    borderWidth: 1
                }
            }
        }
    }
});

const App = () => {
    const [chartData, setChartData] = React.useState(mockStates[0]);
    const [zoomEnabled, setZoomEnabled] = React.useState(false);

    const peaks = findPeaks(chartData.datasets[0].data);
    const peakDataset = {
        label: "Peaks",
        data: peaks.map(p => ({ x: chartData.labels[p.x], y: p.y })),
        type: "scatter",
        pointBackgroundColor: "red",
        pointRadius: 6,
        showLine: false
    };

    const displayData = {
        ...chartData,
        datasets: [
            chartData.datasets[0],
            peakDataset
        ]
    };

    React.useEffect(() => {
        const handleKeyDown = (e) => {
            if (e.key === "1") setChartData(mockStates[0]);
            if (e.key === "2") setChartData(mockStates[1]);
            if (e.key === "3") setChartData(mockStates[2]);
        };
        window.addEventListener("keydown", handleKeyDown);
        return () => window.removeEventListener("keydown", handleKeyDown);
    }, []);

    return (
        <div>
            <label>
                <input
                    type="checkbox"
                    checked={zoomEnabled}
                    onChange={e => setZoomEnabled(e.target.checked)}
                />
                Enable drag zoom (disables move)
            </label>
            <Line data={displayData} options={getOptions(zoomEnabled)} />
            <p>Ine data pomocou 1 (stepped, ako v spectre) 2 3 na klavesnici</p>
        </div>
    );
};

export default App;
